import React, { useState, useMemo } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "./ui/card";
import { Button } from "./ui/button";
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from "./ui/tooltip";
import { AlertTriangle, TrendingUp, DollarSign, Shield, Info, BarChart3, Activity, Target, AlertCircle } from "lucide-react";
import { RISK_INDICATORS } from "../constants/indicatorDescriptions";
import { 
  calculateInstitutionalSmartMoneyScore, 
  calculateInstitutionalTradingSignal,
  detectMarketRegime,
  calculateOptimalPositionSize,
  backtestSignalPerformance
} from "../utils/institutionalAnalytics";

export default function InstitutionalDashboard({ data, unitScale }) {
  const [showRiskAlert, setShowRiskAlert] = useState(false);

  // 포트폴리오 가정 금액 (예시)
  const FUND_SIZE = 100000000; // 1억원
  const latest = data[data.length - 1] || {};
  const latestIndex = data.length - 1;

  // Advanced Analytics Calculations
  const smartMoneyScore = useMemo(() => {
    if (data.length < 60) return { score: 0, breakdown: {} };
    return calculateInstitutionalSmartMoneyScore(data, latestIndex);
  }, [data, latestIndex]);

  const tradingSignal = useMemo(() => {
    if (data.length < 60) return { signal: 0, factors: [], positionSize: {} };
    return calculateInstitutionalTradingSignal(data, latestIndex);
  }, [data, latestIndex]);

  const marketRegime = useMemo(() => {
    if (data.length < 60) return 'UNKNOWN';
    return detectMarketRegime(data, latestIndex);
  }, [data, latestIndex]);

  const optimalPosition = useMemo(() => {
    if (data.length < 60) return { size: 0, leverage: 1, reasoning: [] };
    return calculateOptimalPositionSize(data, latestIndex, FUND_SIZE);
  }, [data, latestIndex]);

  const signalBacktest = useMemo(() => {
    if (data.length < 100) return null;
    return backtestSignalPerformance(data);
  }, [data]);

  // 리스크 계산
  const calculations = useMemo(() => {
    if (!data || data.length < 20) return {};

    // 변동성 계산 (20일)
    const returns = [];
    for (let i = 1; i < Math.min(data.length, 21); i++) {
      const ret = (data[i].close - data[i-1].close) / data[i-1].close;
      returns.push(ret);
    }

    const avgReturn = returns.reduce((a, b) => a + b, 0) / returns.length;
    const variance = returns.reduce((sum, ret) => sum + Math.pow(ret - avgReturn, 2), 0) / returns.length;
    const volatility = Math.sqrt(variance) * Math.sqrt(252); // 연율화

    // VaR 계산 (95% 신뢰수준)
    const dailyVaR = volatility / Math.sqrt(252) * 1.65;

    // 일평균 거래대금 (최근 20일)
    const avgVolume = data.slice(-20).reduce((sum, d) => sum + (d.close * d.volume), 0) / 20;

    // 최대 안전 거래 가능 금액 (일평균의 5%)
    const safeTradeSize = avgVolume * 0.05;

    // 현재 포지션 가정 (전체 펀드의 0.1%)
    const positionSize = FUND_SIZE * 0.001;
    const positionVaR = positionSize * dailyVaR;

    return {
      volatility: volatility * 100,
      dailyVaR: dailyVaR * 100,
      positionVaR,
      avgVolume,
      safeTradeSize,
      positionSize,
      sharpeRatio: avgReturn > 0 ? (avgReturn * 252) / volatility : 0
    };
  }, [data]);

  // 수급 점수 계산 (기관/외인 vs 개인)
  const flowScore = useMemo(() => {
    if (!latest._flows) return 0;

    const foreignFlow = latest._flows.외국인 || 0;
    const institutionFlow = latest._flows.기관합계 || 0;
    const retailFlow = latest._flows.개인 || 0;
    const smartMoney = foreignFlow + institutionFlow;

    // 전체 거래 금액 대비 비율로 점수 계산
    const totalFlow = Math.abs(foreignFlow) + Math.abs(institutionFlow) + Math.abs(retailFlow);
    if (totalFlow === 0) return 0;

    // 스마트머니의 비중 (-100 ~ 100)
    const smartMoneyRatio = (smartMoney / totalFlow) * 100;

    // 개인과의 역관계 보너스
    let score = smartMoneyRatio;
    if (smartMoney > 0 && retailFlow < 0) {
      score = Math.min(100, score * 1.5); // 최대 100
    } else if (smartMoney < 0 && retailFlow > 0) {
      score = Math.max(-100, score * 1.5); // 최소 -100
    }

    return Math.round(score);
  }, [latest]);

  const formatKRW = (num) => {
    const absNum = Math.abs(num);
    if (absNum >= 1000000000000) return `${(num / 1000000000000).toFixed(1)}조원`;
    if (absNum >= 100000000) return `${(num / 100000000).toFixed(1)}억원`;
    if (absNum >= 10000) return `${(num / 10000).toFixed(0)}만원`;
    return `${num.toLocaleString()}원`;
  };

  return (
    <TooltipProvider>
      <div className="space-y-4">
        {/* 헤더 */}
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-2xl font-bold flex items-center gap-2">
            <BarChart3 className="w-6 h-6" />
            Quant Trading Dashboard
          </h2>
          <div className="flex items-center gap-2">
            <span className={`px-3 py-1 rounded-full text-sm font-medium ${
              marketRegime === 'BULL_TRENDING' ? 'bg-green-100 text-green-800' :
              marketRegime === 'BEAR_TRENDING' ? 'bg-red-100 text-red-800' :
              marketRegime === 'HIGH_VOLATILITY' ? 'bg-orange-100 text-orange-800' :
              marketRegime === 'RANGE_BOUND' ? 'bg-gray-100 text-gray-800' :
              marketRegime === 'BREAKOUT_POTENTIAL' ? 'bg-purple-100 text-purple-800' :
              'bg-blue-100 text-blue-800'
            }`}>
              Market: {marketRegime?.replace('_', ' ')}
            </span>
          </div>
        </div>

        {/* Primary Signal Card */}
        <Card className="border-2 border-purple-300 bg-gradient-to-r from-purple-50 to-indigo-50">
          <CardContent className="p-6">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {/* Trading Signal */}
              <div className="text-center">
                <h3 className="text-sm font-medium text-gray-600 mb-2">Trading Signal</h3>
                <div className={`text-4xl font-bold mb-2 ${
                  tradingSignal.signal > 30 ? 'text-green-600' :
                  tradingSignal.signal < -30 ? 'text-red-600' :
                  'text-yellow-600'
                }`}>
                  {tradingSignal.signal?.toFixed(0) || 0}
                </div>
                <div className={`px-3 py-1 rounded-full text-sm font-medium inline-block ${
                  tradingSignal.recommendation?.action === 'STRONG BUY' ? 'bg-green-600 text-white' :
                  tradingSignal.recommendation?.action === 'BUY' ? 'bg-green-500 text-white' :
                  tradingSignal.recommendation?.action === 'ACCUMULATE' ? 'bg-green-400 text-white' :
                  tradingSignal.recommendation?.action === 'HOLD' ? 'bg-yellow-500 text-white' :
                  tradingSignal.recommendation?.action === 'REDUCE' ? 'bg-orange-500 text-white' :
                  tradingSignal.recommendation?.action === 'SELL' ? 'bg-red-500 text-white' :
                  tradingSignal.recommendation?.action === 'EXIT' ? 'bg-red-600 text-white' :
                  'bg-gray-500 text-white'
                }`}>
                  {tradingSignal.recommendation?.action || 'WAIT'}
                </div>
                <p className="text-xs text-gray-600 mt-1">
                  {tradingSignal.recommendation?.description}
                </p>
              </div>

              {/* Smart Money Score */}
              <div className="text-center">
                <h3 className="text-sm font-medium text-gray-600 mb-2">Smart Money Score</h3>
                <div className={`text-4xl font-bold mb-2 ${
                  smartMoneyScore.score > 65 ? 'text-blue-600' :
                  smartMoneyScore.score < 35 ? 'text-red-600' :
                  'text-gray-600'
                }`}>
                  {smartMoneyScore.score?.toFixed(0) || 0}
                </div>
                <p className="text-xs text-gray-600">
                  {smartMoneyScore.interpretation}
                </p>
              </div>

              {/* Position Sizing */}
              <div className="text-center">
                <h3 className="text-sm font-medium text-gray-600 mb-2">Optimal Position</h3>
                <div className="text-2xl font-bold text-indigo-600 mb-1">
                  {formatKRW(optimalPosition.size)}
                </div>
                <p className="text-xs text-gray-600">
                  Leverage: {optimalPosition.leverage}x
                </p>
                <p className="text-xs text-gray-500">
                  {tradingSignal.recommendation?.targetAllocation}
                </p>
              </div>
            </div>

            {/* Signal Confidence Bar */}
            <div className="mt-4 pt-4 border-t border-purple-200">
              <div className="flex items-center justify-between mb-1">
                <span className="text-sm font-medium">Signal Confidence</span>
                <span className="text-sm font-bold">
                  {tradingSignal.confidence || tradingSignal.recommendation?.confidence || '0%'}
                </span>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-2">
                <div 
                  className="bg-gradient-to-r from-purple-500 to-indigo-500 h-2 rounded-full transition-all duration-500"
                  style={{ width: `${tradingSignal.confidence || 0}%` }}
                />
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Signal Factors Breakdown */}
        <Card className="border-2 border-indigo-200">
          <CardHeader>
            <CardTitle className="flex items-center gap-2 text-indigo-700">
              <Activity className="w-5 h-5" />
              Signal Components Analysis
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
              {tradingSignal.factors?.map((factor, idx) => (
                <div key={idx} className="p-3 bg-gray-50 rounded-lg">
                  <div className="flex items-center justify-between mb-1">
                    <span className="text-sm font-medium">{factor.name}</span>
                    <Tooltip>
                      <TooltipTrigger>
                        <Info className="w-3 h-3 text-gray-400" />
                      </TooltipTrigger>
                      <TooltipContent>
                        <p>Weight: {(factor.weight * 100).toFixed(0)}%</p>
                        {factor.description && <p>{factor.description}</p>}
                      </TooltipContent>
                    </Tooltip>
                  </div>
                  <div className="flex items-center gap-2">
                    <div className="flex-1 bg-gray-200 rounded-full h-2">
                      <div 
                        className={`h-2 rounded-full transition-all duration-300 ${
                          factor.value > 0 ? 'bg-green-500' : 'bg-red-500'
                        }`}
                        style={{ 
                          width: `${Math.min(100, Math.abs(factor.value))}%`,
                          marginLeft: factor.value < 0 ? 'auto' : '0'
                        }}
                      />
                    </div>
                    <span className={`text-sm font-bold min-w-[3rem] text-right ${
                      factor.value > 0 ? 'text-green-600' : 
                      factor.value < 0 ? 'text-red-600' : 'text-gray-600'
                    }`}>
                      {typeof factor.value === 'number' ? factor.value.toFixed(0) : factor.value}
                    </span>
                  </div>
                  {factor.phase && (
                    <p className="text-xs text-gray-500 mt-1">{factor.phase}</p>
                  )}
                </div>
              ))}
            </div>

            {/* Backtest Performance */}
            {signalBacktest && (
              <div className="mt-4 p-4 bg-indigo-50 rounded-lg">
                <h4 className="font-medium mb-2 flex items-center gap-2">
                  <Target className="w-4 h-4" />
                  Signal Performance (Last 60 days)
                </h4>
                <div className="grid grid-cols-3 gap-4 text-sm">
                  <div>
                    <span className="text-gray-600">Accuracy:</span>
                    <p className="font-bold text-lg">{signalBacktest.accuracy.toFixed(1)}%</p>
                  </div>
                  <div>
                    <span className="text-gray-600">Total Signals:</span>
                    <p className="font-bold text-lg">{signalBacktest.totalSignals}</p>
                  </div>
                  <div>
                    <span className="text-gray-600">Correct:</span>
                    <p className="font-bold text-lg">{signalBacktest.correctSignals}</p>
                  </div>
                </div>
              </div>
            )}
          </CardContent>
        </Card>

        {/* 리스크 관리 대시보드 */}
        <Card className="border-2 border-red-200">
          <CardHeader>
            <CardTitle className="flex items-center gap-2 text-red-700">
              <Shield className="w-5 h-5" />
              리스크 관리 센터
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              {/* VaR */}
              <div className="p-4 bg-red-50 rounded-lg">
                <div className="flex items-center gap-2 mb-2">
                  <h3 className="font-semibold">일일 VaR</h3>
                  <Tooltip>
                    <TooltipTrigger>
                      <Info className="w-4 h-4 text-gray-500" />
                    </TooltipTrigger>
                    <TooltipContent className="max-w-xs">
                      <p className="font-semibold mb-1">{RISK_INDICATORS.VaR.name}</p>
                      <p>{RISK_INDICATORS.VaR.expert}</p>
                    </TooltipContent>
                  </Tooltip>
                </div>
                <p className="text-2xl font-bold text-red-600">
                  {calculations.dailyVaR?.toFixed(2)}%
                </p>
                <p className="text-sm text-gray-600">
                  포지션 VaR: {formatKRW(calculations.positionVaR || 0)}
                </p>
              </div>

              {/* 유동성 */}
              <div className="p-4 bg-blue-50 rounded-lg">
                <div className="flex items-center gap-2 mb-2">
                  <h3 className="font-semibold">유동성 분석</h3>
                  <Tooltip>
                    <TooltipTrigger>
                      <Info className="w-4 h-4 text-gray-500" />
                    </TooltipTrigger>
                    <TooltipContent className="max-w-xs">
                      <p className="font-semibold mb-1">{RISK_INDICATORS.LIQUIDITY.name}</p>
                      <p>{RISK_INDICATORS.LIQUIDITY.expert}</p>
                    </TooltipContent>
                  </Tooltip>
                </div>
                <p className="text-sm">일평균 거래대금</p>
                <p className="text-xl font-bold">{formatKRW(calculations.avgVolume || 0)}</p>
                <p className="text-sm text-blue-600 mt-1">
                  안전 거래 한도: {formatKRW(calculations.safeTradeSize || 0)}
                </p>
              </div>

              {/* 샤프 비율 */}
              <div className="p-4 bg-green-50 rounded-lg">
                <div className="flex items-center gap-2 mb-2">
                  <h3 className="font-semibold">샤프 비율</h3>
                  <Tooltip>
                    <TooltipTrigger>
                      <Info className="w-4 h-4 text-gray-500" />
                    </TooltipTrigger>
                    <TooltipContent className="max-w-xs">
                      <p className="font-semibold mb-1">{RISK_INDICATORS.SHARPE.name}</p>
                      <p>{RISK_INDICATORS.SHARPE.expert}</p>
                    </TooltipContent>
                  </Tooltip>
                </div>
                <p className="text-2xl font-bold text-green-600">
                  {calculations.sharpeRatio?.toFixed(2) || "N/A"}
                </p>
                <p className="text-sm text-gray-600">
                  {calculations.sharpeRatio > 2 ? "우수" : 
                   calculations.sharpeRatio > 1 ? "양호" : 
                   calculations.sharpeRatio > 0.5 ? "보통" : "미흡"}
                </p>
              </div>
            </div>

            {/* 리스크 경고 */}
            {calculations.dailyVaR > 3 && (
              <div className="mt-4 p-4 bg-red-100 border border-red-300 rounded-lg flex items-center gap-2">
                <AlertTriangle className="w-5 h-5 text-red-600" />
                <p className="text-red-700 font-medium">
                  경고: 높은 변동성 감지. 포지션 축소를 검토하세요.
                </p>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Enhanced Smart Money Flow Analysis */}
        <Card className="border-2 border-blue-200">
          <CardHeader>
            <CardTitle className="flex items-center gap-2 text-blue-700">
              <DollarSign className="w-5 h-5" />
              Smart Money Flow Analysis
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {/* Smart Money Score Breakdown */}
              <div className="p-4 bg-gradient-to-r from-blue-50 to-cyan-50 rounded-lg">
                <h3 className="font-semibold mb-3">Score Components</h3>
                <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
                  {Object.entries(smartMoneyScore.breakdown || {}).map(([key, value]) => (
                    <div key={key} className="text-sm">
                      <span className="text-gray-600 capitalize">
                        {key.replace(/([A-Z])/g, ' $1').trim()}:
                      </span>
                      <span className={`ml-2 font-bold ${
                        value > 0 ? 'text-green-600' : 
                        value < 0 ? 'text-red-600' : 'text-gray-600'
                      }`}>
                        {typeof value === 'number' ? value.toFixed(1) : value}
                      </span>
                    </div>
                  ))}
                </div>
              </div>

              {/* 주체별 상세 */}
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <h4 className="font-medium mb-2 flex items-center gap-2">
                    스마트머니
                    <Tooltip>
                      <TooltipTrigger>
                        <Info className="w-4 h-4 text-gray-500" />
                      </TooltipTrigger>
                      <TooltipContent className="max-w-xs">
                        <p>외국인 + 기관의 합계. 정보력과 자금력을 갖춘 투자 주체입니다.</p>
                      </TooltipContent>
                    </Tooltip>
                  </h4>
                  <div className="space-y-2">
                    <div className="flex justify-between">
                      <span>외국인</span>
                      <span className={`font-bold ${(latest._flows?.외국인 || 0) > 0 ? 'text-red-600' : 'text-blue-600'}`}>
                        {formatKRW(latest._flows?.외국인 || 0)}
                      </span>
                    </div>
                    <div className="flex justify-between">
                      <span>기관</span>
                      <span className={`font-bold ${(latest._flows?.기관합계 || 0) > 0 ? 'text-red-600' : 'text-blue-600'}`}>
                        {formatKRW(latest._flows?.기관합계 || 0)}
                      </span>
                    </div>
                  </div>
                </div>
                <div>
                  <h4 className="font-medium mb-2">개인</h4>
                  <div className="flex justify-between">
                    <span>개인</span>
                    <span className={`font-bold ${(latest._flows?.개인 || 0) > 0 ? 'text-red-600' : 'text-blue-600'}`}>
                      {formatKRW(latest._flows?.개인 || 0)}
                    </span>
                  </div>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Advanced Position Management */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <TrendingUp className="w-5 h-5" />
              Risk-Adjusted Position Management
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="p-4 bg-gradient-to-br from-green-50 to-emerald-50 rounded-lg">
                <h4 className="font-medium mb-2 flex items-center gap-2">
                  Kelly-Optimized Position
                  <Tooltip>
                    <TooltipTrigger>
                      <Info className="w-4 h-4 text-gray-400" />
                    </TooltipTrigger>
                    <TooltipContent className="max-w-xs">
                      <p>Position size calculated using Modified Kelly Criterion with 25% fractional Kelly for safety</p>
                    </TooltipContent>
                  </Tooltip>
                </h4>
                <p className="text-2xl font-bold text-green-700 mb-2">
                  {formatKRW(optimalPosition.size)}
                </p>
                <div className="space-y-1 text-xs text-gray-600">
                  {optimalPosition.reasoning?.map((reason, idx) => (
                    <p key={idx}>• {reason}</p>
                  ))}
                </div>
              </div>

              <div className="p-4 bg-gradient-to-br from-red-50 to-orange-50 rounded-lg">
                <h4 className="font-medium mb-2 flex items-center gap-2">
                  Dynamic Risk Controls
                  <Tooltip>
                    <TooltipTrigger>
                      <Info className="w-4 h-4 text-gray-400" />
                    </TooltipTrigger>
                    <TooltipContent className="max-w-xs">
                      <p>Risk limits adjusted based on market regime and volatility</p>
                    </TooltipContent>
                  </Tooltip>
                </h4>
                <div className="space-y-2">
                  <div>
                    <span className="text-sm text-gray-600">Stop Loss:</span>
                    <p className="font-bold text-red-600">
                      {tradingSignal.recommendation?.stopLoss || '진입가 -5%'}
                    </p>
                  </div>
                  <div>
                    <span className="text-sm text-gray-600">Target Allocation:</span>
                    <p className="font-bold text-orange-600">
                      {tradingSignal.recommendation?.targetAllocation || '5-10%'}
                    </p>
                  </div>
                  <div>
                    <span className="text-sm text-gray-600">Risk Multiplier:</span>
                    <p className="font-bold">
                      {tradingSignal.riskMultiplier?.toFixed(2) || '1.00'}x
                    </p>
                  </div>
                </div>
              </div>
            </div>

            {/* Risk Warnings */}
            {marketRegime === 'HIGH_VOLATILITY' && (
              <div className="mt-4 p-3 bg-orange-100 border border-orange-300 rounded-lg flex items-center gap-2">
                <AlertCircle className="w-5 h-5 text-orange-600 flex-shrink-0" />
                <p className="text-sm text-orange-700">
                  High volatility detected. Position sizes automatically reduced by {((1 - (tradingSignal.riskMultiplier || 0.7)) * 100).toFixed(0)}%
                </p>
              </div>
            )}

            {calculations.dailyVaR > 4 && (
              <div className="mt-4 p-3 bg-red-100 border border-red-300 rounded-lg flex items-center gap-2">
                <AlertTriangle className="w-5 h-5 text-red-600 flex-shrink-0" />
                <p className="text-sm text-red-700">
                  Extreme risk level. Consider reducing exposure or hedging positions.
                </p>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </TooltipProvider>
  );
}
