// ADX (Average Directional Index) Í≥ÑÏÇ∞
function calculateADX(data, index, period = 14) {
  if (index < period * 2) return 0;
  let plusDM = 0,
    minusDM = 0,
    tr = 0;
  for (let i = index - period + 1; i <= index; i++) {
    const curr = data[i];
    const prev = data[i - 1];
    const highDiff = curr.high - prev.high;
    const lowDiff = prev.low - curr.low;
    if (highDiff > lowDiff && highDiff > 0) plusDM += highDiff;
    if (lowDiff > highDiff && lowDiff > 0) minusDM += lowDiff;
    const trueRange = Math.max(curr.high - curr.low, Math.abs(curr.high - prev.close), Math.abs(curr.low - prev.close));
    tr += trueRange;
  }
  const plusDI = plusDM / tr * 100;
  const minusDI = minusDM / tr * 100;
  const dx = Math.abs(plusDI - minusDI) / (plusDI + minusDI) * 100;
  return dx;
}

// Stochastic Ïò§Ïã§Î†àÏù¥ÌÑ∞ Í≥ÑÏÇ∞
function calculateStochastic(data, index, period = 14) {
  if (index < period) return 50;
  const slice = data.slice(index - period + 1, index + 1);
  const highs = slice.map(d => d.high);
  const lows = slice.map(d => d.low);
  const highest = Math.max(...highs);
  const lowest = Math.min(...lows);
  const current = data[index].close;
  if (highest === lowest) return 50;
  return (current - lowest) / (highest - lowest) * 100;
}

// Î≥µÌï© Ïò§Ïã§Î†àÏù¥ÌÑ∞ Ï†êÏàò
function calculateOscillatorComposite(rsi, mfi, stochastic) {
  let score = 0;

  // RSI Ïã†Ìò∏
  if (rsi < 30) score += 30;else if (rsi < 40) score += 15;else if (rsi > 70) score -= 30;else if (rsi > 60) score -= 15;

  // MFI Ïã†Ìò∏
  if (mfi < 20) score += 25;else if (mfi < 35) score += 10;else if (mfi > 80) score -= 25;else if (mfi > 65) score -= 10;

  // Stochastic Ïã†Ìò∏
  if (stochastic < 20) score += 20;else if (stochastic < 35) score += 10;else if (stochastic > 80) score -= 20;else if (stochastic > 65) score -= 10;

  // Îã§Ïù¥Î≤ÑÏ†ÑÏä§ Î≥¥ÎÑàÏä§
  if (rsi < 40 && mfi < 40 && stochastic < 40) score *= 1.5;
  if (rsi > 60 && mfi > 60 && stochastic > 60) score *= 1.5;
  return Math.max(-100, Math.min(100, score));
}

// Î≥ÄÎèôÏÑ± Ï°∞Ï†ï ÏàòÏùµÎ•†
function calculateVolatilityAdjustedReturn(data, index, lookback = 20) {
  if (index < lookback) return 0;
  const returns = [];
  for (let i = index - lookback + 1; i <= index; i++) {
    const ret = (data[i].close - data[i - 1].close) / data[i - 1].close;
    returns.push(ret);
  }
  const avgReturn = returns.reduce((a, b) => a + b, 0) / returns.length;
  const variance = returns.reduce((sum, ret) => sum + Math.pow(ret - avgReturn, 2), 0) / returns.length;
  const volatility = Math.sqrt(variance);
  if (volatility === 0) return 0;

  // Sharpe Ratio Ïä§ÌÉÄÏùº Í≥ÑÏÇ∞
  return avgReturn / volatility;
}

// ÏãúÏû• ÎØ∏ÏãúÍµ¨Ï°∞ Î∂ÑÏÑù
function analyzeMarketMicrostructure(data, index) {
  if (index < 20) return 0;
  const current = data[index];
  const prev20 = data.slice(index - 20, index);
  let score = 0;

  // 1. Í∞ÄÍ≤© Ìö®Ïú®ÏÑ± (ÏñºÎßàÎÇò Î∂ÄÎìúÎüΩÍ≤å ÏõÄÏßÅÏù¥ÎäîÍ∞Ä)
  const priceEfficiency = calculatePriceEfficiency(prev20);
  score += priceEfficiency * 0.3;

  // 2. Í±∞ÎûòÎüâ ÌîÑÎ°úÌååÏùº (Ï†ïÏÉÅ Î∂ÑÌè¨ vs Ïù¥ÏÉÅ Ìå®ÌÑ¥)
  const volumeProfile = analyzeVolumeProfile(prev20, current);
  score += volumeProfile * 0.3;

  // 3. Ïä§ÌîÑÎ†àÎìú Ï∂îÏ†ï (Í≥†Ï†Ä Ï∞®Ïù¥ Í∏∞Î∞ò)
  const spreadQuality = estimateSpreadQuality(current, prev20);
  score += spreadQuality * 0.4;
  return score;
}

// Í∞ÄÍ≤© Ìö®Ïú®ÏÑ± Í≥ÑÏÇ∞
function calculatePriceEfficiency(data) {
  if (data.length < 2) return 0;
  let totalMove = 0;
  let netMove = Math.abs(data[data.length - 1].close - data[0].close);
  for (let i = 1; i < data.length; i++) {
    totalMove += Math.abs(data[i].close - data[i - 1].close);
  }
  if (totalMove === 0) return 0;

  // Ìö®Ïú®ÏÑ±Ïù¥ ÎÜíÏùÑÏàòÎ°ù Ï¢ãÏùå (1Ïóê Í∞ÄÍπåÏö∏ÏàòÎ°ù ÏßÅÏÑ† ÏõÄÏßÅÏûÑ)
  const efficiency = netMove / totalMove;
  return efficiency > 0.7 ? 1 : efficiency > 0.3 ? 0.5 : -0.5;
}

// Í±∞ÎûòÎüâ ÌîÑÎ°úÌååÏùº Î∂ÑÏÑù
function analyzeVolumeProfile(historical, current) {
  const avgVolume = historical.reduce((sum, d) => sum + d.volume, 0) / historical.length;
  const volumeRatio = current.volume / avgVolume;

  // Ï†ÅÏ†ï Í±∞ÎûòÎüâ (ÌèâÍ∑†Ïùò 0.8 ~ 2Î∞∞)
  if (volumeRatio >= 0.8 && volumeRatio <= 2) {
    return 0.5;
  } else if (volumeRatio > 3) {
    // Ïù¥ÏÉÅ Í±∞ÎûòÎüâ
    return current.close > historical[historical.length - 1].close ? 1 : -1;
  } else if (volumeRatio < 0.5) {
    // Í±∞ÎûòÎüâ Î∂ÄÏ°±
    return -0.5;
  }
  return 0;
}

// Ïä§ÌîÑÎ†àÎìú ÌíàÏßà Ï∂îÏ†ï
function estimateSpreadQuality(current, historical) {
  const avgRange = historical.reduce((sum, d) => sum + (d.high - d.low), 0) / historical.length;
  const currentRange = current.high - current.low;

  // Ïä§ÌîÑÎ†àÎìúÍ∞Ä ÌèâÍ∑†Î≥¥Îã§ Ï¢ÅÏúºÎ©¥ Ïú†ÎèôÏÑ± Ï¢ãÏùå
  if (currentRange < avgRange * 0.7) return 1;
  if (currentRange < avgRange) return 0.5;
  if (currentRange > avgRange * 1.5) return -1;
  return 0;
}

// ÏúÑÌóò Ï°∞Ï†ï Í≥ÑÏàò
function calculateRiskAdjustment(data, index) {
  if (index < 60) return 1;
  const recent20 = data.slice(index - 20, index);
  const volatility = calculateVolatility(recent20);

  // Î≥ÄÎèôÏÑ±Ïóê Îî∞Î•∏ Ï°∞Ï†ï (ÎÜíÏùÄ Î≥ÄÎèôÏÑ± = ÎÇÆÏùÄ Ïã†Î¢∞ÎèÑ)
  if (volatility > 0.03) return 0.7; // Ïùº 3% Ïù¥ÏÉÅ Î≥ÄÎèôÏÑ±
  if (volatility > 0.02) return 0.85;
  if (volatility < 0.01) return 1.1; // ÎÇÆÏùÄ Î≥ÄÎèôÏÑ± = ÎÜíÏùÄ Ïã†Î¢∞ÎèÑ

  return 1;
}

// Î≥ÄÎèôÏÑ± Í≥ÑÏÇ∞
function calculateVolatility(data) {
  if (data.length < 2) return 0;
  const returns = [];
  for (let i = 1; i < data.length; i++) {
    returns.push((data[i].close - data[i - 1].close) / data[i - 1].close);
  }
  const avg = returns.reduce((a, b) => a + b, 0) / returns.length;
  const variance = returns.reduce((sum, ret) => sum + Math.pow(ret - avg, 2), 0) / returns.length;
  return Math.sqrt(variance);
}

// Ïã†Ìò∏ Ïã†Î¢∞ÎèÑ Í≥ÑÏÇ∞
function calculateSignalConfidence(factors) {
  // Î™®Îì† Ìå©ÌÑ∞Í∞Ä Í∞ôÏùÄ Î∞©Ìñ•ÏùÑ Í∞ÄÎ¶¨ÌÇ§Î©¥ ÎÜíÏùÄ Ïã†Î¢∞ÎèÑ
  const positiveFactors = factors.filter(f => f.value > 0).length;
  const negativeFactors = factors.filter(f => f.value < 0).length;
  const unanimity = Math.max(positiveFactors, negativeFactors) / factors.length;
  const avgStrength = factors.reduce((sum, f) => sum + Math.abs(f.value), 0) / factors.length;
  return Math.round((unanimity * 0.6 + avgStrength / 100 * 0.4) * 100);
}

// Ïä§ÎßàÌä∏Î®∏Îãà Ìï¥ÏÑù (Ìà¨ÏûêÏûê ÏπúÌôîÏ†Å)
function getSmartMoneyInterpretation(score) {
  if (score >= 80) return "üí∞ Í∏∞Í¥Ä/Ïô∏Ïù∏ ÎåÄÎüâ Îß§Ïßë Ï§ë ‚Üí Í∞ïÎ†• Îß§Ïàò ÏãúÍ∑∏ÎÑê";
  if (score >= 65) return "üìà Ïä§ÎßàÌä∏Î®∏Îãà ÏàúÎß§Ïàò ‚Üí Îß§Ïàò Í≥†Î†§";
  if (score >= 50) return "‚öñÔ∏è ÏàòÍ∏â Í∑†Ìòï ‚Üí Í¥ÄÎßù Ï∂îÏ≤ú";
  if (score >= 35) return "üìâ Ïä§ÎßàÌä∏Î®∏Îãà Ïù¥ÌÉà ÏãúÏûë ‚Üí Ï£ºÏùò ÌïÑÏöî";
  return "üö® Í∏∞Í¥Ä/Ïô∏Ïù∏ ÎåÄÎüâ Îß§ÎèÑ ‚Üí Îß§ÎèÑ Í≥†Î†§";
}

// Enhanced Trading Recommendation with Risk Management
function getTradingRecommendation(signal) {
  const urgency = Math.abs(signal) > 70 ? "Ï¶âÏãú" : "Îã®Í≥ÑÏ†Å";
  const confidence = Math.min(100, Math.abs(signal) * 1.2);
  if (signal >= 70) {
    return {
      action: "STRONG BUY",
      description: `${urgency} Îß§Ïàò Ìè¨ÏßÄÏÖò Íµ¨Ï∂ï`,
      targetAllocation: "15-20%",
      stopLoss: "ÏßÑÏûÖÍ∞Ä -3%",
      confidence: `${confidence}%`
    };
  } else if (signal >= 40) {
    return {
      action: "BUY",
      description: "Î∂ÑÌï† Îß§Ïàò ÏßÑÌñâ",
      targetAllocation: "10-15%",
      stopLoss: "ÏßÑÏûÖÍ∞Ä -5%",
      confidence: `${confidence}%`
    };
  } else if (signal >= 15) {
    return {
      action: "ACCUMULATE",
      description: "Ï†ÄÍ∞Ä Î∂ÑÌï†Îß§Ïàò Í≤ÄÌÜ†",
      targetAllocation: "5-10%",
      stopLoss: "ÏßÑÏûÖÍ∞Ä -7%",
      confidence: `${confidence}%`
    };
  } else if (signal >= -15) {
    return {
      action: "HOLD",
      description: "ÌòÑ Ìè¨ÏßÄÏÖò Ïú†ÏßÄ",
      targetAllocation: "ÌòÑÏû¨ Ïú†ÏßÄ",
      stopLoss: "ÎèôÏ†Å Ï°∞Ï†ï",
      confidence: `${confidence}%`
    };
  } else if (signal >= -40) {
    return {
      action: "REDUCE",
      description: "Îã®Í≥ÑÏ†Å ÎπÑÏ§ë Ï∂ïÏÜå",
      targetAllocation: "50% Í∞êÏÜå",
      stopLoss: "Ï¶âÏãú Ïã§Ìñâ",
      confidence: `${confidence}%`
    };
  } else if (signal >= -70) {
    return {
      action: "SELL",
      description: "Ìè¨ÏßÄÏÖò Ï≤≠ÏÇ∞ ÏßÑÌñâ",
      targetAllocation: "20% Ïù¥Ìïò",
      stopLoss: "N/A",
      confidence: `${confidence}%`
    };
  }
  return {
    action: "EXIT",
    description: `${urgency} Ï†ÑÎüâ Ï≤≠ÏÇ∞`,
    targetAllocation: "0%",
    stopLoss: "N/A",
    confidence: `${confidence}%`
  };
}

// Enhanced VWAP Í≥ÑÏÇ∞ (Îã§Ï§ë ÏïµÏª§ ÏßÄÏõê)
export function calculateEnhancedVWAP(data, anchorIndices = []) {
  if (!data || data.length === 0) return data;

  // Í∏∞Î≥∏ ÏïµÏª§: ÏãúÏûëÏ†ê, ÏµúÍ∑º Í≥†Ï†ê, ÏµúÍ∑º Ï†ÄÏ†ê
  if (anchorIndices.length === 0) {
    anchorIndices = [0]; // Í∏∞Î≥∏Í∞í

    // ÏµúÍ∑º 60Ïùº Í≥†Ï†ê/Ï†ÄÏ†ê Ï∞æÍ∏∞
    if (data.length > 60) {
      const recent60 = data.slice(-60);
      let highIndex = 0,
        lowIndex = 0;
      let highPrice = 0,
        lowPrice = Infinity;
      recent60.forEach((d, i) => {
        const globalIndex = data.length - 60 + i;
        if (d.high > highPrice) {
          highPrice = d.high;
          highIndex = globalIndex;
        }
        if (d.low < lowPrice) {
          lowPrice = d.low;
          lowIndex = globalIndex;
        }
      });
      anchorIndices.push(highIndex, lowIndex);
    }
  }

  // Í∞Å ÏïµÏª§Î≥Ñ VWAP Í≥ÑÏÇ∞
  const result = data.map(d => ({
    ...d
  }));
  anchorIndices.forEach((anchorIndex, anchorNum) => {
    let cumPV = 0;
    let cumV = 0;
    for (let i = 0; i < result.length; i++) {
      if (i < anchorIndex) {
        result[i][`vwap${anchorNum}`] = null;
        continue;
      }
      const typicalPrice = (result[i].high + result[i].low + result[i].close) / 3;
      const volume = result[i].volume || 0;
      cumPV += typicalPrice * volume;
      cumV += volume;
      result[i][`vwap${anchorNum}`] = cumV > 0 ? cumPV / cumV : typicalPrice;
    }
  });

  // Í∏∞Î≥∏ VWAP (Ï≤´ Î≤àÏß∏ ÏïµÏª§)
  result.forEach(d => {
    d.avwap = d.vwap0;
  });
  return result;
}

// Backtesting Signal Performance
export function backtestSignalPerformance(data, lookback = 60) {
  if (!data || data.length < lookback + 20) return null;
  const results = [];
  let correctSignals = 0;
  let totalSignals = 0;
  for (let i = lookback; i < data.length - 5; i++) {
    const signal = calculateInstitutionalTradingSignal(data, i);
    const futureReturn = (data[i + 5].close - data[i].close) / data[i].close;
    if (Math.abs(signal.signal) > 30) {
      totalSignals++;
      const correct = signal.signal > 0 && futureReturn > 0 || signal.signal < 0 && futureReturn < 0;
      if (correct) correctSignals++;
      results.push({
        date: data[i].date,
        signal: signal.signal,
        actualReturn: futureReturn * 100,
        correct
      });
    }
  }
  return {
    accuracy: totalSignals > 0 ? correctSignals / totalSignals * 100 : 0,
    totalSignals,
    correctSignals,
    recentPerformance: results.slice(-10)
  };
}
const institutionalAnalytics = {
  calculateInstitutionalSmartMoneyScore,
  calculateInstitutionalTradingSignal,
  calculateEnhancedVWAP,
  detectMarketRegime,
  calculateOptimalPositionSize,
  backtestSignalPerformance
};
export default institutionalAnalytics;

