}

// Enhanced VWAP Í≥ÑÏÇ∞ (Îã§Ï§ë ÏïµÏª§ ÏßÄÏõê)
export function calculateEnhancedVWAP(data, anchorIndices = []) {
  if (!data || data.length === 0) return data;

  // Í∏∞Î≥∏ ÏïµÏª§: ÏãúÏûëÏ†ê, ÏµúÍ∑º Í≥†Ï†ê, ÏµúÍ∑º Ï†ÄÏ†ê
  if (anchorIndices.length === 0) {
    anchorIndices = [0]; // Í∏∞Î≥∏Í∞í

    // ÏµúÍ∑º 60Ïùº Í≥†Ï†ê/Ï†ÄÏ†ê Ï∞æÍ∏∞
    if (data.length > 60) {
      const recent60 = data.slice(-60);
      let highIndex = 0, lowIndex = 0;
      let highPrice = 0, lowPrice = Infinity;

      recent60.forEach((d, i) => {
        const globalIndex = data.length - 60 + i;
        if (d.high > highPrice) {
          highPrice = d.high;
          highIndex = globalIndex;
        }
        if (d.low < lowPrice) {
          lowPrice = d.low;
          lowIndex = globalIndex;
        }
      });

      anchorIndices.push(highIndex, lowIndex);
    }
  }

  // Í∞Å ÏïµÏª§Î≥Ñ VWAP Í≥ÑÏÇ∞
  const result = data.map(d => ({ ...d }));

  anchorIndices.forEach((anchorIndex, anchorNum) => {
    let cumPV = 0;
    let cumV = 0;

    for (let i = 0; i < result.length; i++) {
      if (i < anchorIndex) {
        result[i][`vwap${anchorNum}`] = null;
        continue;
      }

      const typicalPrice = (result[i].high + result[i].low + result[i].close) / 3;
      const volume = result[i].volume || 0;

      cumPV += typicalPrice * volume;
      cumV += volume;

      result[i][`vwap${anchorNum}`] = cumV > 0 ? cumPV / cumV : typicalPrice;
    }
  });

  // Í∏∞Î≥∏ VWAP (Ï≤´ Î≤àÏß∏ ÏïµÏª§)
  result.forEach(d => {
    d.avwap = d.vwap0;
  });

  return result;
}

// Backtesting Signal Performance
export function backtestSignalPerformance(data, lookback = 60) {
  if (!data || data.length < lookback + 20) return null;

  const results = [];
  let correctSignals = 0;
  let totalSignals = 0;

  for (let i = lookback; i < data.length - 5; i++) {
    const signal = calculateInstitutionalTradingSignal(data, i);
    const futureReturn = (data[i + 5].close - data[i].close) / data[i].close;

    if (Math.abs(signal.signal) > 30) {
      totalSignals++;
      const correct = (signal.signal > 0 && futureReturn > 0) || 
                     (signal.signal < 0 && futureReturn < 0);
      if (correct) correctSignals++;

      results.push({
        date: data[i].date,
        signal: signal.signal,
        actualReturn: futureReturn * 100,
        correct
      });
    }
  }

  return {
    accuracy: totalSignals > 0 ? (correctSignals / totalSignals * 100) : 0,
    totalSignals,
    correctSignals,
    recentPerformance: results.slice(-10)
  };
}

