import React, { useMemo, useState, useCallback } from "react";
import { motion } from "framer-motion";
import { LineChart as LineChartIcon, Building2, Brain, User } from "lucide-react";
import { Button } from "./components/ui/button";
import { TooltipProvider } from "./components/ui/tooltip";

// Utilities
import { DEFAULT_SELECTED_CATEGORIES, DEFAULT_UNIT_SCALE } from "./constants/sampleData";
import { useDataProcessing } from "./hooks/useDataProcessing";
import { useSelfTest } from "./hooks/useSelfTest";

// Components
import PeriodSelector from "./components/controls/PeriodSelector";
import DataLoader from "./components/controls/DataLoader";
import FlowDisplayOptions from "./components/controls/FlowDisplayOptions";
import CSVInputs from "./components/controls/CSVInputs";
// Charts
import AdvancedPriceChart from "./components/charts/AdvancedPriceChart";
import EnhancedInvestorFlowChart from "./components/charts/EnhancedInvestorFlowChart";
import EnhancedIndicatorsChart from "./components/charts/EnhancedIndicatorsChart";
import EnhancedOBVFlowChart from "./components/charts/EnhancedOBVFlowChart";
import VolumeProfileChart from "./components/charts/VolumeProfileChart";
import SelfTest from "./components/SelfTest";
import InstitutionalDashboard from "./components/InstitutionalDashboard";

export default function ParadiseFlowDashboard() {
  // State management
  const [priceCSV, setPriceCSV] = useState("");
  const [flowCSV, setFlowCSV] = useState("");
  const [anchorIndex, setAnchorIndex] = useState(0);
  const [days, setDays] = useState(60);
  const [useSample, setUseSample] = useState(true);
  const [unitScale, setUnitScale] = useState(DEFAULT_UNIT_SCALE);
  const [selectedCats, setSelectedCats] = useState(DEFAULT_SELECTED_CATEGORIES);
  const [showInstitutional, setShowInstitutional] = useState(false);

  // Custom hooks
  const { enriched } = useDataProcessing(priceCSV, flowCSV, useSample, anchorIndex);
  const { testLog, runSelfTests } = useSelfTest();

  // Time range slice
  const viewData = useMemo(() => {
    if (!enriched || enriched.length === 0) return [];
    const start = Math.max(0, enriched.length - days);
    return enriched.slice(start);
  }, [enriched, days]);

  // Computed values
  const unitName = useMemo(() => (
    unitScale === 1 ? "원" : 
    unitScale === 1000000 ? "백만원" : 
    unitScale === 100000000 ? "억원" : 
    `x${unitScale}`
  ), [unitScale]);

  // Event handlers
  const toggleCat = useCallback((k) => {
    setSelectedCats((prev) => (prev.includes(k) ? prev.filter((x) => x !== k) : [...prev, k]));
  }, []);

  const handleChartClick = useCallback((e) => {
    if (e && e.activeTooltipIndex != null) {
      setAnchorIndex(enriched.indexOf(viewData[e.activeTooltipIndex]));
    }
  }, [enriched, viewData]);

  return (
    <TooltipProvider>
      <div className="w-full min-h-screen p-4 md:p-6 bg-gradient-to-b from-slate-50 to-white">
        <div className="mx-auto max-w-7xl space-y-6">
          <motion.div initial={{ opacity: 0, y: 12 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.4 }}>
            <div className="flex justify-between items-start">
              <div>
                <h1 className="text-2xl md:text-3xl font-bold tracking-tight flex items-center gap-3">
                  <LineChartIcon className="w-7 h-7" /> PARADISE (034230) — Flow & Price Dashboard
                </h1>
                <p className="text-slate-600 mt-1">
                  전문 투자자를 위한 고급 분석 대시보드 • 데이터 기반 투자 의사결정 지원
                </p>
              </div>
              <div className="flex gap-2">
                <Button
                  variant={showInstitutional ? "default" : "secondary"}
                  onClick={() => setShowInstitutional(!showInstitutional)}
                >
                  <Building2 className="w-4 h-4 mr-2" />
                  고급 분석
                </Button>
                <Button
                  variant={expertMode ? "default" : "secondary"}
                  onClick={() => setExpertMode(!expertMode)}
                  size="sm"
                >
                  {expertMode ? <Brain className="w-4 h-4 mr-2" /> : <User className="w-4 h-4 mr-2" />}
                  {expertMode ? "전문가" : "초보자"}
                </Button>
              </div>
            </div>
          </motion.div>

        {/* Controls */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <PeriodSelector days={days} setDays={setDays} />
          <DataLoader 
            setUseSample={setUseSample} 
            setPriceCSV={setPriceCSV} 
            setFlowCSV={setFlowCSV} 
            setAnchorIndex={setAnchorIndex} 
          />
          <FlowDisplayOptions 
            unitScale={unitScale} 
            setUnitScale={setUnitScale} 
            selectedCats={selectedCats} 
            toggleCat={toggleCat} 
          />
        </div>

          {/* CSV inputs (only when using own CSV) */}
          {!useSample && (
            <CSVInputs 
              priceCSV={priceCSV} 
              setPriceCSV={setPriceCSV} 
              flowCSV={flowCSV} 
              setFlowCSV={setFlowCSV} 
            />
          )}

          {/* Institutional Dashboard */}
          {showInstitutional && (
            <InstitutionalDashboard data={viewData} unitScale={unitScale} />
          )}

          {/* Main Charts */}
          <AdvancedPriceChart data={viewData} onChartClick={handleChartClick} expertMode={expertMode} />

          {/* Volume Analysis */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-4">
            <VolumeProfileChart data={viewData} expertMode={expertMode} />
            <EnhancedOBVFlowChart data={viewData} unitScale={unitScale} expertMode={expertMode} />
          </div>

          {/* Investor Flow */}
          <EnhancedInvestorFlowChart data={viewData} selectedCats={selectedCats} unitScale={unitScale} unitName={unitName} expertMode={expertMode} />

          {/* Technical Indicators */}
          <EnhancedIndicatorsChart data={viewData} expertMode={expertMode} />

        {/* Self Test */}
        <SelfTest testLog={testLog} runSelfTests={runSelfTests} />

        {/* Footer Note */}
        <div className="text-xs text-slate-500 leading-relaxed">
          ⚠️ 면책: 상기 샘플 데이터는 시각화 확인용 예시입니다. 실제 매매 판단 시에는 반드시 최신 공식 데이터(CSV)를 로드하여 확인하세요. 
          CSV 업로드가 어려우면 채팅창에 "데이터 가져와줘"라고 입력해 주세요. 제가 최신 데이터를 검색·정리해 여기에 즉시 반영해 드립니다.
        </div>
        </div>
      </div>
    </TooltipProvider>
  );
}
