// Market Regime Detection
export function detectMarketRegime(data, index) {
  if (!data || index < 20) return 'UNKNOWN';

  const prev20 = data.slice(Math.max(0, index - 20), index + 1);
  const prev60 = data.slice(Math.max(0, index - 60), index + 1);

  // 1. Trend Analysis
