// 1. ì„¸ë ¥ ë§¤ì§‘ íŒ¨í„´ ê°ì§€
export function detectAccumulationPattern(data, index) {
  if (!data || index < 20) return { detected: false, confidence: 0 };

  const recent20 = data.slice(Math.max(0, index - 19), index + 1);
  let signals = 0;

