  const smartMoneyBuy = recent20.filter(d => 
    (d._flows?.ê¸°ê´€í•©ê³„ || 0) + (d._flows?.ì™¸êµ­ì¸í•©ê³„ || 0) > 0 &&
    (d._flows?.ê°œì¸ || 0) < 0
