import React, { useState, useMemo } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "./ui/card";
import { Button } from "./ui/button";
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from "./ui/tooltip";
import { AlertTriangle, TrendingUp, DollarSign, Shield, Info, BarChart3 } from "lucide-react";
import { RISK_INDICATORS } from "../constants/indicatorDescriptions";

export default function InstitutionalDashboard({ data, unitScale }) {
  const [showRiskAlert, setShowRiskAlert] = useState(false);

  // 포트폴리오 가정 금액 (예시)
  const FUND_SIZE = 100000000; // 1억원
  const latest = data[data.length - 1] || {};

  // 리스크 계산
  const calculations = useMemo(() => {
    if (!data || data.length < 20) return {};

    // 변동성 계산 (20일)
    const returns = [];
    for (let i = 1; i < Math.min(data.length, 21); i++) {
      const ret = (data[i].close - data[i-1].close) / data[i-1].close;
      returns.push(ret);
    }

    const avgReturn = returns.reduce((a, b) => a + b, 0) / returns.length;
    const variance = returns.reduce((sum, ret) => sum + Math.pow(ret - avgReturn, 2), 0) / returns.length;
    const volatility = Math.sqrt(variance) * Math.sqrt(252); // 연율화

    // VaR 계산 (95% 신뢰수준)
    const dailyVaR = volatility / Math.sqrt(252) * 1.65;

    // 일평균 거래대금 (최근 20일)
    const avgVolume = data.slice(-20).reduce((sum, d) => sum + (d.close * d.volume), 0) / 20;

    // 최대 안전 거래 가능 금액 (일평균의 5%)
    const safeTradeSize = avgVolume * 0.05;

    // 현재 포지션 가정 (전체 펀드의 0.1%)
    const positionSize = FUND_SIZE * 0.001;
    const positionVaR = positionSize * dailyVaR;

    return {
      volatility: volatility * 100,
      dailyVaR: dailyVaR * 100,
      positionVaR,
      avgVolume,
      safeTradeSize,
      positionSize,
      sharpeRatio: avgReturn > 0 ? (avgReturn * 252) / volatility : 0
    };
  }, [data]);

  // 수급 점수 계산 (기관/외인 vs 개인)
  const flowScore = useMemo(() => {
    if (!latest._flows) return 0;

    const foreignFlow = latest._flows.외국인 || 0;
    const institutionFlow = latest._flows.기관합계 || 0;
    const retailFlow = latest._flows.개인 || 0;
    const smartMoney = foreignFlow + institutionFlow;

    // 전체 거래 금액 대비 비율로 점수 계산
    const totalFlow = Math.abs(foreignFlow) + Math.abs(institutionFlow) + Math.abs(retailFlow);
    if (totalFlow === 0) return 0;

    // 스마트머니의 비중 (-100 ~ 100)
    const smartMoneyRatio = (smartMoney / totalFlow) * 100;

    // 개인과의 역관계 보너스
    let score = smartMoneyRatio;
    if (smartMoney > 0 && retailFlow < 0) {
      score = Math.min(100, score * 1.5); // 최대 100
    } else if (smartMoney < 0 && retailFlow > 0) {
      score = Math.max(-100, score * 1.5); // 최소 -100
    }

    return Math.round(score);
  }, [latest]);

  const formatKRW = (num) => {
    if (num >= 1000000000000) return `${(num / 1000000000000).toFixed(1)}조원`;
    if (num >= 100000000) return `${(num / 100000000).toFixed(1)}억원`;
    return `${(num / 10000).toFixed(0)}만원`;
  };

  return (
    <TooltipProvider>
      <div className="space-y-4">
        {/* 헤더 */}
        <h2 className="text-2xl font-bold flex items-center gap-2">
          <BarChart3 className="w-6 h-6" />
          고급 투자 분석 대시보드
        </h2>

        {/* 리스크 관리 대시보드 */}
        <Card className="border-2 border-red-200">
          <CardHeader>
            <CardTitle className="flex items-center gap-2 text-red-700">
              <Shield className="w-5 h-5" />
              리스크 관리 센터
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              {/* VaR */}
              <div className="p-4 bg-red-50 rounded-lg">
                <div className="flex items-center gap-2 mb-2">
                  <h3 className="font-semibold">일일 VaR</h3>
                  <Tooltip>
                    <TooltipTrigger>
                      <Info className="w-4 h-4 text-gray-500" />
                    </TooltipTrigger>
                    <TooltipContent className="max-w-xs">
                      <p className="font-semibold mb-1">{RISK_INDICATORS.VaR.name}</p>
                      <p>{RISK_INDICATORS.VaR.expert}</p>
                    </TooltipContent>
                  </Tooltip>
                </div>
                <p className="text-2xl font-bold text-red-600">
                  {calculations.dailyVaR?.toFixed(2)}%
                </p>
                <p className="text-sm text-gray-600">
                  포지션 VaR: {formatKRW(calculations.positionVaR || 0)}
                </p>
              </div>

              {/* 유동성 */}
              <div className="p-4 bg-blue-50 rounded-lg">
                <div className="flex items-center gap-2 mb-2">
                  <h3 className="font-semibold">유동성 분석</h3>
                  <Tooltip>
                    <TooltipTrigger>
                      <Info className="w-4 h-4 text-gray-500" />
                    </TooltipTrigger>
                    <TooltipContent className="max-w-xs">
                      <p className="font-semibold mb-1">{RISK_INDICATORS.LIQUIDITY.name}</p>
                      <p>{RISK_INDICATORS.LIQUIDITY.expert}</p>
                    </TooltipContent>
                  </Tooltip>
                </div>
                <p className="text-sm">일평균 거래대금</p>
                <p className="text-xl font-bold">{formatKRW(calculations.avgVolume || 0)}</p>
                <p className="text-sm text-blue-600 mt-1">
                  안전 거래 한도: {formatKRW(calculations.safeTradeSize || 0)}
                </p>
              </div>

              {/* 샤프 비율 */}
              <div className="p-4 bg-green-50 rounded-lg">
                <div className="flex items-center gap-2 mb-2">
                  <h3 className="font-semibold">샤프 비율</h3>
                  <Tooltip>
                    <TooltipTrigger>
                      <Info className="w-4 h-4 text-gray-500" />
                    </TooltipTrigger>
                    <TooltipContent className="max-w-xs">
                      <p className="font-semibold mb-1">{RISK_INDICATORS.SHARPE.name}</p>
                      <p>{RISK_INDICATORS.SHARPE.expert}</p>
                    </TooltipContent>
                  </Tooltip>
                </div>
                <p className="text-2xl font-bold text-green-600">
                  {calculations.sharpeRatio?.toFixed(2) || "N/A"}
                </p>
                <p className="text-sm text-gray-600">
                  {calculations.sharpeRatio > 2 ? "우수" : 
                   calculations.sharpeRatio > 1 ? "양호" : 
                   calculations.sharpeRatio > 0.5 ? "보통" : "미흡"}
                </p>
              </div>
            </div>

            {/* 리스크 경고 */}
            {calculations.dailyVaR > 3 && (
              <div className="mt-4 p-4 bg-red-100 border border-red-300 rounded-lg flex items-center gap-2">
                <AlertTriangle className="w-5 h-5 text-red-600" />
                <p className="text-red-700 font-medium">
                  경고: 높은 변동성 감지. 포지션 축소를 검토하세요.
                </p>
              </div>
            )}
          </CardContent>
        </Card>

        {/* 스마트머니 플로우 분석 */}
        <Card className="border-2 border-blue-200">
          <CardHeader>
            <CardTitle className="flex items-center gap-2 text-blue-700">
              <DollarSign className="w-5 h-5" />
              스마트머니 플로우 분석
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {/* 수급 점수 */}
              <div className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
                <div>
                  <h3 className="font-semibold mb-1">오늘의 수급 점수</h3>
                  <p className="text-sm text-gray-600">
                    Smart Money vs Retail Money Divergence
                  </p>
                </div>
                <div className={`text-3xl font-bold ${flowScore > 0 ? 'text-green-600' : 'text-red-600'}`}>
                  {flowScore > 0 ? '+' : ''}{flowScore}
                </div>
              </div>

              {/* 주체별 상세 */}
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <h4 className="font-medium mb-2 flex items-center gap-2">
                    스마트머니
                    <Tooltip>
                      <TooltipTrigger>
                        <Info className="w-4 h-4 text-gray-500" />
                      </TooltipTrigger>
                      <TooltipContent className="max-w-xs">
                        <p>외국인 + 기관의 합계. 정보력과 자금력을 갖춘 투자 주체입니다.</p>
                      </TooltipContent>
                    </Tooltip>
                  </h4>
                  <div className="space-y-2">
                    <div className="flex justify-between">
                      <span>외국인</span>
                      <span className={`font-bold ${(latest._flows?.외국인 || 0) > 0 ? 'text-red-600' : 'text-blue-600'}`}>
                        {formatKRW(latest._flows?.외국인 || 0)}
                      </span>
                    </div>
                    <div className="flex justify-between">
                      <span>기관</span>
                      <span className={`font-bold ${(latest._flows?.기관합계 || 0) > 0 ? 'text-red-600' : 'text-blue-600'}`}>
                        {formatKRW(latest._flows?.기관합계 || 0)}
                      </span>
                    </div>
                  </div>
                </div>
                <div>
                  <h4 className="font-medium mb-2">개인</h4>
                  <div className="flex justify-between">
                    <span>개인</span>
                    <span className={`font-bold ${(latest._flows?.개인 || 0) > 0 ? 'text-red-600' : 'text-blue-600'}`}>
                      {formatKRW(latest._flows?.개인 || 0)}
                    </span>
                  </div>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* 포지션 관리 도구 */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <TrendingUp className="w-5 h-5" />
              포지션 관리 계산기
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="p-4 bg-gray-50 rounded-lg">
                <h4 className="font-medium mb-2">권장 포지션 크기</h4>
                <p className="text-sm text-gray-600 mb-1">
                  Kelly Criterion 기반 (보수적 25% 적용)
                </p>
                <p className="text-xl font-bold">
                  포트폴리오의 1% ~ 3%
                </p>
                <p className="text-sm text-gray-500 mt-1">
                  변동성과 유동성에 따라 조정
                </p>
              </div>
              <div className="p-4 bg-gray-50 rounded-lg">
                <h4 className="font-medium mb-2">손절선 제안</h4>
                <p className="text-sm text-gray-600 mb-1">
                  2 ATR 기준
                </p>
                <p className="text-xl font-bold">
                  {latest.close ? `${(latest.close * 0.95).toFixed(0)}원` : "N/A"}
                </p>
                <p className="text-sm text-gray-500 mt-1">
                  현재가 대비 -5%
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </TooltipProvider>
  );
}
