import React from "react";
import {
  ResponsiveContainer,
  ComposedChart,
  Bar,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip as RechartsTooltip,
  Legend,
} from "recharts";
import { Card, CardContent, CardHeader, CardTitle } from "../ui/card";
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from "../ui/tooltip";
import { Info, TrendingUp, TrendingDown, Users } from "lucide-react";
import { fmtDate, toNum } from "../../utils/dataUtils";
import { INVESTOR_COLORS } from "../../constants/chartColors";

export default function EnhancedInvestorFlowChart({ data, selectedCats, unitScale, unitName }) {
  const latest = data[data.length - 1] || {};

  // Ïò§ÎäòÏùò ÏàòÍ∏â Ï£ºÏ≤¥Î≥Ñ ÏàúÏúÑ
  const todayFlows = selectedCats.map(cat => ({
    name: cat,
    value: latest._flows?.[cat] || (cat === "Ïô∏Íµ≠Ïù∏Ìï©Í≥Ñ" ? 
      (toNum(latest._flows?.["Ïô∏Íµ≠Ïù∏"]) + toNum(latest._flows?.["Í∏∞ÌÉÄÏô∏Íµ≠Ïù∏"])) : 0),
    color: INVESTOR_COLORS[cat]
  })).sort((a, b) => Math.abs(b.value) - Math.abs(a.value));

  // ÏµúÍ∑º 5Ïùº ÎàÑÏ†Å ÏàòÍ∏â
  const recent5Days = data.slice(-5);
  const cumulative5Days = selectedCats.map(cat => {
    const sum = recent5Days.reduce((acc, d) => {
      const value = d._flows?.[cat] || (cat === "Ïô∏Íµ≠Ïù∏Ìï©Í≥Ñ" ? 
        (toNum(d._flows?.["Ïô∏Íµ≠Ïù∏"]) + toNum(d._flows?.["Í∏∞ÌÉÄÏô∏Íµ≠Ïù∏"])) : 0);
      return acc + value;
    }, 0);
    return { name: cat, value: sum, color: INVESTOR_COLORS[cat] };
  }).sort((a, b) => b.value - a.value);

  const formatValue = (value, isScaled = true) => {
    // If isScaled is false, we need to scale the value
    const displayValue = isScaled ? value : value / unitScale;
    if (unitScale === 100000000) return `${displayValue.toFixed(1)}Ïñµ`;
    if (unitScale === 1000000) return `${displayValue.toFixed(0)}Î∞±Îßå`;
    if (unitScale === 1) return `${displayValue.toLocaleString()}Ïõê`;
    return displayValue.toLocaleString();
  };

  return (
    <TooltipProvider>
      <div className="space-y-4">
        {/* ÏùºÎ≥Ñ ÏàòÍ∏â Ï∞®Ìä∏ */}
        <Card className="shadow-md">
          <CardHeader>
            <CardTitle className="flex items-center gap-2 text-base">
              <Users className="w-4 h-4"/>
              Ìà¨ÏûêÏ£ºÏ≤¥Î≥Ñ ÏùºÎ≥Ñ ÏàòÍ∏â / Îã®ÏúÑ: {unitName}
              <Tooltip>
                <TooltipTrigger>
                  <Info className="w-4 h-4 text-gray-500" />
                </TooltipTrigger>
                <TooltipContent className="max-w-sm">
                  <p className="font-semibold mb-1">Ìà¨ÏûêÏ£ºÏ≤¥Î≥Ñ ÏàúÎß§Ïàò Î∂ÑÏÑù</p>
                  <p className="text-sm mb-2">
                    Í∞Å Ìà¨ÏûêÏ£ºÏ≤¥Ïùò ÏùºÎ≥Ñ ÏàúÎß§Ïàò Í∏àÏï°ÏùÑ ÏãúÍ∞ÅÌôîÌï©ÎãàÎã§. Ïä§ÎßàÌä∏Î®∏Îãà(Ïô∏Íµ≠Ïù∏+Í∏∞Í¥Ä)ÏôÄ Í∞úÏù∏Ïùò divergenceÍ∞Ä ÌïµÏã¨ÏûÖÎãàÎã§.
                  </p>
                  <div className="text-xs space-y-1">
                    <p>‚Ä¢ <span className="text-red-600">ÏúÑÏ™Ω ÎßâÎåÄ</span>: ÏàúÎß§Ïàò (Îß§Ïàò > Îß§ÎèÑ)</p>
                    <p>‚Ä¢ <span className="text-blue-600">ÏïÑÎûòÏ™Ω ÎßâÎåÄ</span>: ÏàúÎß§ÎèÑ (Îß§ÎèÑ > Îß§Ïàò)</p>
                  </div>
                </TooltipContent>
              </Tooltip>
            </CardTitle>
          </CardHeader>
          <CardContent>
            {/* Ïò§ÎäòÏùò ÏàòÍ∏â ÏöîÏïΩ */}
            <div className="mb-4 p-3 bg-gray-50 rounded-lg">
              <div className="flex justify-between items-center mb-2">
                <h4 className="text-sm font-semibold">Ïò§ÎäòÏùò ÏàòÍ∏â TOP 3</h4>
                <span className="text-xs text-gray-500">{fmtDate(latest.date)}</span>
              </div>
              <div className="space-y-1">
                {todayFlows.slice(0, 3).map((flow, idx) => (
                  <div key={flow.name} className="flex justify-between items-center">
                    <span className="text-sm" style={{ color: flow.color }}>
                      {idx + 1}. {flow.name}
                    </span>
                    <span className={`text-sm font-bold ${flow.value > 0 ? 'text-red-600' : 'text-blue-600'}`}>
                      {flow.value > 0 ? '+' : ''}{formatValue(flow.value, false)}
                    </span>
                  </div>
                ))}
              </div>
            </div>

            {/* Ï∞®Ìä∏ */}
            <div className="h-72">
              <ResponsiveContainer>
                <ComposedChart data={data} stackOffset="sign">
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey={(d) => fmtDate(d.date)} tick={{ fontSize: 12 }} />
                  <YAxis tick={{ fontSize: 12 }} domain={["auto", "auto"]} />
                  <RechartsTooltip 
                    formatter={(v, name) => {
                      if (typeof v === "number") {
                        return [formatValue(v), name];
                      }
                      return v;
                    }}
                    contentStyle={{ backgroundColor: 'rgba(255, 255, 255, 0.95)', border: '1px solid #e5e7eb' }}
                  />
                  <Legend />
                  {selectedCats.map((k) => (
                    <Bar 
                      key={k} 
                      stackId="flows" 
                      name={k} 
                      fill={INVESTOR_COLORS[k]}
                      dataKey={(d) => (d._flows && (d._flows[k] ?? 
                        (k === "Ïô∏Íµ≠Ïù∏Ìï©Í≥Ñ" ? (toNum(d._flows["Ïô∏Íµ≠Ïù∏"]) + toNum(d._flows["Í∏∞ÌÉÄÏô∏Íµ≠Ïù∏"])) : 0))) / unitScale} 
                    />
                  ))}
                </ComposedChart>
              </ResponsiveContainer>
            </div>

            {/* 5Ïùº ÎàÑÏ†Å ÏöîÏïΩ */}
            <div className="mt-4 p-3 bg-blue-50 rounded-lg">
              <h4 className="text-sm font-semibold mb-2">ÏµúÍ∑º 5Ïùº ÎàÑÏ†Å ÏàòÍ∏â</h4>
              <div className="grid grid-cols-3 gap-2">
                {cumulative5Days.map(flow => (
                  <div key={flow.name} className="text-center">
                    <p className="text-xs" style={{ color: flow.color }}>{flow.name}</p>
                    <p className={`text-sm font-bold ${flow.value > 0 ? 'text-red-600' : 'text-blue-600'}`}>
                      {flow.value > 0 ? '+' : ''}{formatValue(flow.value, false)}
                    </p>
                  </div>
                ))}
              </div>
            </div>
          </CardContent>
        </Card>

        {/* ÎàÑÏ†Å ÏàòÍ∏â Ï∞®Ìä∏ */}
        <Card className="shadow-md">
          <CardHeader>
            <CardTitle className="flex items-center gap-2 text-base">
              Ìà¨ÏûêÏ£ºÏ≤¥Î≥Ñ ÎàÑÏ†Å ÏàòÍ∏â Ï∂îÏù¥ / Îã®ÏúÑ: {unitName}
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="h-72">
              <ResponsiveContainer>
                <ComposedChart data={data}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey={(d) => fmtDate(d.date)} tick={{ fontSize: 12 }} />
                  <YAxis tick={{ fontSize: 12 }} domain={["auto", "auto"]} />
                  <RechartsTooltip 
                    formatter={(v, name) => {
                      if (typeof v === "number") {
                        return [formatValue(v), name];
                      }
                      return v;
                    }}
                    contentStyle={{ backgroundColor: 'rgba(255, 255, 255, 0.95)', border: '1px solid #e5e7eb' }}
                  />
                  <Legend />
                  {selectedCats.map((k) => (
                    <Line 
                      key={k} 
                      type="monotone" 
                      dot={false} 
                      name={k} 
                      stroke={INVESTOR_COLORS[k]}
                      strokeWidth={2}
                      dataKey={(d) => (d._cum && (d._cum[k] ?? 
                        (k === "Ïô∏Íµ≠Ïù∏Ìï©Í≥Ñ" ? (toNum(d._cum["Ïô∏Íµ≠Ïù∏"]) + toNum(d._cum["Í∏∞ÌÉÄÏô∏Íµ≠Ïù∏"])) : 0))) / unitScale} 
                    />
                  ))}
                </ComposedChart>
              </ResponsiveContainer>
            </div>

            <div className="mt-4 p-3 bg-amber-50 rounded-lg">
              <p className="text-xs font-semibold text-amber-800 mb-1">üí° Ï†ÑÎ¨∏Í∞Ä ÌåÅ</p>
              <p className="text-xs text-amber-700">
                ‚Ä¢ Ïô∏Íµ≠Ïù∏Í≥º Í∏∞Í¥ÄÏù¥ ÎèôÏãúÏóê Îß§ÏàòÌïòÎ©¥ Í∞ïÌïú ÏÉÅÏäπ Ïã†Ìò∏<br/>
                ‚Ä¢ Í∞úÏù∏ Îß§ÎèÑ + Í∏∞Í¥Ä Îß§Ïàò = Í∏∞Í¥ÄÏùò Ï†ÄÍ∞Ä Îß§Ïßë Í∞ÄÎä•ÏÑ±<br/>
                ‚Ä¢ ÎàÑÏ†Å ÏàòÍ∏âÏù¥ divergenceÎ•º Î≥¥Ïù¥Î©¥ Ï∂îÏÑ∏ Ï†ÑÌôò ÏûÑÎ∞ï
              </p>
            </div>
          </CardContent>
        </Card>
      </div>
    </TooltipProvider>
  );
}
