import React, { useMemo } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "./ui/card";
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from "./ui/tooltip";
import { AlertTriangle, TrendingUp, DollarSign, Shield, Info, BarChart3, Activity, Target, AlertCircle } from "lucide-react";
import { RISK_INDICATORS } from "../constants/indicatorDescriptions";
import { 
  calculateInstitutionalSmartMoneyScore, 
  calculateInstitutionalTradingSignal,
  detectMarketRegime,
  calculateOptimalPositionSize,
  backtestSignalPerformance
} from "../utils/institutionalAnalytics";

export default function InstitutionalDashboard({ data, unitScale }) {
  // Ìè¨Ìä∏Ìè¥Î¶¨Ïò§ Í∞ÄÏ†ï Í∏àÏï° (ÏòàÏãú)
  const FUND_SIZE = 100000000; // 1ÏñµÏõê
  const latestIndex = data.length - 1;
  const latest = useMemo(() => data[latestIndex] || {}, [data, latestIndex]);

  // Advanced Analytics Calculations
  const smartMoneyScore = useMemo(() => {
    if (!data || data.length < 20) return { score: 50, breakdown: {}, interpretation: 'Îç∞Ïù¥ÌÑ∞ Î∂ÄÏ°±' };
    return calculateInstitutionalSmartMoneyScore(data, latestIndex);
  }, [data, latestIndex]);

  const tradingSignal = useMemo(() => {
    if (!data || data.length < 20) return { 
      signal: 0, 
      factors: [], 
      positionSize: {},
      recommendation: { action: 'WAIT', description: 'Îç∞Ïù¥ÌÑ∞ ÏàòÏßë Ï§ë' },
      confidence: 0
    };
    return calculateInstitutionalTradingSignal(data, latestIndex) || {
      signal: 0,
      factors: [],
      positionSize: {},
      recommendation: { action: 'WAIT', description: 'Î∂ÑÏÑù Ï§ë' },
      confidence: 0
    };
  }, [data, latestIndex]);

  const marketRegime = useMemo(() => {
    if (!data || data.length < 20) return 'UNKNOWN';
    return detectMarketRegime(data, latestIndex);
  }, [data, latestIndex]);

  const optimalPosition = useMemo(() => {
    if (!data || data.length < 20) return { size: 0, leverage: 1, reasoning: ['Îç∞Ïù¥ÌÑ∞ Î∂ÄÏ°±'] };
    return calculateOptimalPositionSize(data, latestIndex, FUND_SIZE);
  }, [data, latestIndex]);

  const signalBacktest = useMemo(() => {
    if (data.length < 100) return null;
    return backtestSignalPerformance(data);
  }, [data]);

  // Î¶¨Ïä§ÌÅ¨ Í≥ÑÏÇ∞
  const calculations = useMemo(() => {
    if (!data || data.length < 20) return {};

    // Î≥ÄÎèôÏÑ± Í≥ÑÏÇ∞ (20Ïùº)
    const returns = [];
    for (let i = 1; i < Math.min(data.length, 21); i++) {
      const ret = (data[i].close - data[i-1].close) / data[i-1].close;
      returns.push(ret);
    }

    const avgReturn = returns.reduce((a, b) => a + b, 0) / returns.length;
    const variance = returns.reduce((sum, ret) => sum + Math.pow(ret - avgReturn, 2), 0) / returns.length;
    const volatility = Math.sqrt(variance) * Math.sqrt(252); // Ïó∞Ïú®Ìôî

    // VaR Í≥ÑÏÇ∞ (95% Ïã†Î¢∞ÏàòÏ§Ä)
    const dailyVaR = volatility / Math.sqrt(252) * 1.65;

    // ÏùºÌèâÍ∑† Í±∞ÎûòÎåÄÍ∏à (ÏµúÍ∑º 20Ïùº)
    const avgVolume = data.slice(-20).reduce((sum, d) => sum + (d.close * d.volume), 0) / 20;

    // ÏµúÎåÄ ÏïàÏ†Ñ Í±∞Îûò Í∞ÄÎä• Í∏àÏï° (ÏùºÌèâÍ∑†Ïùò 5%)
    const safeTradeSize = avgVolume * 0.05;

    // ÌòÑÏû¨ Ìè¨ÏßÄÏÖò Í∞ÄÏ†ï (Ï†ÑÏ≤¥ ÌéÄÎìúÏùò 0.1%)
    const positionSize = FUND_SIZE * 0.001;
    const positionVaR = positionSize * dailyVaR;

    return {
      volatility: volatility * 100,
      dailyVaR: dailyVaR * 100,
      positionVaR,
      avgVolume,
      safeTradeSize,
      positionSize,
      sharpeRatio: avgReturn > 0 ? (avgReturn * 252) / volatility : 0
    };
  }, [data]);

  // Removed unused flowScore calculation as it's now replaced by smartMoneyScore

  const formatKRW = (num) => {
    const absNum = Math.abs(num);
    if (absNum >= 1000000000000) return `${(num / 1000000000000).toFixed(1)}Ï°∞Ïõê`;
    if (absNum >= 100000000) return `${(num / 100000000).toFixed(1)}ÏñµÏõê`;
    if (absNum >= 10000) return `${(num / 10000).toFixed(0)}ÎßåÏõê`;
    return `${num.toLocaleString()}Ïõê`;
  };

  return (
    <TooltipProvider>
      <div className="space-y-4">
        {/* Ìó§Îçî */}
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-2xl font-bold flex items-center gap-2">
            <BarChart3 className="w-6 h-6" />
            AI Ìà¨Ïûê Î∂ÑÏÑù ÎåÄÏãúÎ≥¥Îìú
          </h2>
          <div className="flex items-center gap-2">
            <span className={`px-3 py-1 rounded-full text-sm font-medium ${
              marketRegime === 'BULL_TRENDING' ? 'bg-green-100 text-green-800' :
              marketRegime === 'BEAR_TRENDING' ? 'bg-red-100 text-red-800' :
              marketRegime === 'HIGH_VOLATILITY' ? 'bg-orange-100 text-orange-800' :
              marketRegime === 'RANGE_BOUND' ? 'bg-gray-100 text-gray-800' :
              marketRegime === 'BREAKOUT_POTENTIAL' ? 'bg-purple-100 text-purple-800' :
              'bg-blue-100 text-blue-800'
            }`}>
              ÏãúÏû• ÏÉÅÌÉú: {marketRegime === 'BULL_TRENDING' ? 'ÏÉÅÏäπ Ï∂îÏÑ∏' :
                      marketRegime === 'BEAR_TRENDING' ? 'ÌïòÎùΩ Ï∂îÏÑ∏' :
                      marketRegime === 'HIGH_VOLATILITY' ? 'Í≥†Î≥ÄÎèôÏÑ±' :
                      marketRegime === 'RANGE_BOUND' ? 'Ìö°Î≥¥ Íµ¨Í∞Ñ' :
                      marketRegime === 'BREAKOUT_POTENTIAL' ? 'ÎèåÌåå Í∞ÄÎä•' :
                      marketRegime === 'TRANSITIONING' ? 'Ï†ÑÌôòÍ∏∞' : 'Î∂ÑÏÑù Ï§ë'}
            </span>
          </div>
        </div>

        {/* ÌïµÏã¨ Ìà¨Ïûê Ïã†Ìò∏ Ïπ¥Îìú */}
        <Card className="border-2 border-purple-300 bg-gradient-to-r from-purple-50 to-indigo-50">
          <CardHeader>
            <CardTitle className="text-lg font-bold">üéØ Ìà¨Ïûê Í≤∞Ï†ï Í∞ÄÏù¥Îìú</CardTitle>
          </CardHeader>
          <CardContent className="p-6">
            {/* Îã®Ïàú Î™ÖÌôïÌïú Ìà¨Ïûê Í≤∞Ï†ï */}
            <div className="mb-6 p-4 bg-white rounded-lg border-2 border-gray-200">
              <h3 className="text-lg font-bold mb-3 text-center">
                ÌòÑÏû¨ Ìà¨Ïûê Ï∂îÏ≤ú: 
                <span className={`ml-2 ${
                  (tradingSignal?.signal || 0) > 40 ? 'text-green-600' :
                  (tradingSignal?.signal || 0) > 15 ? 'text-green-500' :
                  (tradingSignal?.signal || 0) > -15 ? 'text-yellow-600' :
                  (tradingSignal?.signal || 0) > -40 ? 'text-orange-600' :
                  'text-red-600'
                }`}>
                  {(tradingSignal?.signal || 0) > 40 ? 'üü¢ Îß§Ïàò' :
                   (tradingSignal?.signal || 0) > 15 ? 'üü¢ Î∂ÑÌï† Îß§Ïàò' :
                   (tradingSignal?.signal || 0) > -15 ? 'üü° Í¥ÄÎßù' :
                   (tradingSignal?.signal || 0) > -40 ? 'üü† ÎπÑÏ§ë Ï∂ïÏÜå' :
                   'üî¥ Îß§ÎèÑ'}
                </span>
              </h3>
              <p className="text-center text-sm text-gray-600">
                {tradingSignal.recommendation?.description || 'Îç∞Ïù¥ÌÑ∞ Î∂ÑÏÑù Ï§ë...'}
              </p>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {/* Ï¢ÖÌï© Îß§Îß§ Ïã†Ìò∏ */}
              <div className="text-center">
                <Tooltip>
                  <TooltipTrigger>
                    <h3 className="text-sm font-medium text-gray-600 mb-2 cursor-help">
                      Ï¢ÖÌï© Îß§Îß§ Ïã†Ìò∏ ‚ìò
                    </h3>
                  </TooltipTrigger>
                  <TooltipContent className="max-w-xs">
                    <p className="font-semibold mb-1">Ï¢ÖÌï© Îß§Îß§ Ïã†Ìò∏ÎûÄ?</p>
                    <p className="text-sm">-100(Í∞ïÎ†• Îß§ÎèÑ) ~ +100(Í∞ïÎ†• Îß§Ïàò)</p>
                    <p className="text-sm mt-1">+40 Ïù¥ÏÉÅ: Îß§Ïàò Ï∂îÏ≤ú</p>
                    <p className="text-sm">-40 Ïù¥Ìïò: Îß§ÎèÑ Ï∂îÏ≤ú</p>
                    <p className="text-sm">-15 ~ +15: Í¥ÄÎßù Íµ¨Í∞Ñ</p>
                  </TooltipContent>
                </Tooltip>
                <div className={`text-4xl font-bold mb-2 ${
                  (tradingSignal?.signal || 0) > 30 ? 'text-green-600' :
                  (tradingSignal?.signal || 0) < -30 ? 'text-red-600' :
                  'text-yellow-600'
                }`}>
                  {(tradingSignal?.signal ?? 0).toFixed(0)}
                </div>
                <div className={`px-3 py-1 rounded-full text-sm font-medium inline-block ${
                  (tradingSignal?.signal || 0) > 40 ? 'bg-green-600 text-white' :
                  (tradingSignal?.signal || 0) > 15 ? 'bg-green-500 text-white' :
                  (tradingSignal?.signal || 0) > -15 ? 'bg-yellow-500 text-white' :
                  (tradingSignal?.signal || 0) > -40 ? 'bg-orange-500 text-white' :
                  'bg-red-600 text-white'
                }`}>
                  {(tradingSignal?.signal || 0) > 40 ? 'Îß§Ïàò' :
                   (tradingSignal?.signal || 0) > 15 ? 'Î∂ÑÌï†Îß§Ïàò' :
                   (tradingSignal?.signal || 0) > -15 ? 'Í¥ÄÎßù' :
                   (tradingSignal?.signal || 0) > -40 ? 'ÎπÑÏ§ëÏ∂ïÏÜå' :
                   'Îß§ÎèÑ'}
                </div>
              </div>

              {/* Ïä§ÎßàÌä∏Î®∏Îãà Ï†êÏàò */}
              <div className="text-center">
                <Tooltip>
                  <TooltipTrigger>
                    <h3 className="text-sm font-medium text-gray-600 mb-2 cursor-help">
                      Ïä§ÎßàÌä∏Î®∏Îãà Ï†êÏàò ‚ìò
                    </h3>
                  </TooltipTrigger>
                  <TooltipContent className="max-w-xs">
                    <p className="font-semibold mb-1">Ïä§ÎßàÌä∏Î®∏Îãà Ï†êÏàòÎûÄ?</p>
                    <p className="text-sm">Í∏∞Í¥Ä+Ïô∏Íµ≠Ïù∏Ïùò ÏàòÍ∏â Í∞ïÎèÑÎ•º Î∂ÑÏÑù</p>
                    <p className="text-sm mt-1">80 Ïù¥ÏÉÅ: Í∏∞Í¥Ä ÎåÄÎüâ Îß§Ïßë</p>
                    <p className="text-sm">65 Ïù¥ÏÉÅ: Ïä§ÎßàÌä∏Î®∏Îãà Îß§Ïàò Ïö∞ÏúÑ</p>
                    <p className="text-sm">35 Ïù¥Ìïò: Ïä§ÎßàÌä∏Î®∏Îãà Ïù¥ÌÉà</p>
                    <p className="text-sm">20 Ïù¥Ìïò: Í∏∞Í¥Ä ÎåÄÎüâ Îß§ÎèÑ</p>
                  </TooltipContent>
                </Tooltip>
                <div className={`text-4xl font-bold mb-2 ${
                  smartMoneyScore.score > 65 ? 'text-blue-600' :
                  smartMoneyScore.score < 35 ? 'text-red-600' :
                  'text-gray-600'
                }`}>
                  {smartMoneyScore.score?.toFixed(0) || 0}
                </div>
                <p className="text-xs text-gray-600 px-2">
                  {smartMoneyScore.interpretation}
                </p>
              </div>

              {/* Ï∂îÏ≤ú Ìè¨ÏßÄÏÖò */}
              <div className="text-center">
                <Tooltip>
                  <TooltipTrigger>
                    <h3 className="text-sm font-medium text-gray-600 mb-2 cursor-help">
                      ÏµúÏ†Å Ìè¨ÏßÄÏÖò ÌÅ¨Í∏∞ ‚ìò
                    </h3>
                  </TooltipTrigger>
                  <TooltipContent className="max-w-xs">
                    <p className="font-semibold mb-1">Ìè¨ÏßÄÏÖò ÌÅ¨Í∏∞ÎûÄ?</p>
                    <p className="text-sm">Kelly Criterion Í∏∞Î∞ò Í≥ÑÏÇ∞</p>
                    <p className="text-sm">Î≥ÄÎèôÏÑ±Í≥º ÏäπÎ•†ÏùÑ Í≥†Î†§Ìïú ÏµúÏ†Å Ìà¨ÏûêÍ∏à</p>
                  </TooltipContent>
                </Tooltip>
                <div className="text-2xl font-bold text-indigo-600 mb-1">
                  {formatKRW(optimalPosition.size)}
                </div>
                <p className="text-xs text-gray-600">
                  Î†àÎ≤ÑÎ¶¨ÏßÄ: {optimalPosition.leverage}x
                </p>
                <p className="text-xs text-gray-500">
                  {tradingSignal.recommendation?.targetAllocation || 'Ìè¨Ìä∏Ìè¥Î¶¨Ïò§Ïùò 5-10%'}
                </p>
              </div>
            </div>

            {/* Ïã†Ìò∏ Ïã†Î¢∞ÎèÑ */}
            <div className="mt-4 pt-4 border-t border-purple-200">
              <div className="flex items-center justify-between mb-1">
                <span className="text-sm font-medium">Ïã†Ìò∏ Ïã†Î¢∞ÎèÑ</span>
                <span className="text-sm font-bold">
                  {tradingSignal.confidence || tradingSignal.recommendation?.confidence || '0%'}
                </span>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-2">
                <div 
                  className="bg-gradient-to-r from-purple-500 to-indigo-500 h-2 rounded-full transition-all duration-500"
                  style={{ width: `${tradingSignal.confidence || 0}%` }}
                />
              </div>
            </div>

            {/* Í∏∞Í∞ÑÎ≥Ñ Ìï¥ÏÑù Í∞ÄÏù¥Îìú */}
            <div className="mt-4 p-3 bg-blue-50 rounded-lg">
              <h4 className="font-medium text-sm mb-2 flex items-center gap-1">
                <Info className="w-4 h-4" />
                Í∏∞Í∞ÑÎ≥Ñ Ï†êÏàò Ìï¥ÏÑù Í∞ÄÏù¥Îìú
              </h4>
              <p className="text-xs text-gray-700 leading-relaxed">
                ‚Ä¢ <strong>30Ïùº/60Ïùº</strong>: Îã®Í∏∞ ÏàòÍ∏â ÌùêÎ¶Ñ Î∞òÏòÅ, Îã®Í∏∞ Ìà¨ÏûêÏóê Ï†ÅÌï©<br/>
                ‚Ä¢ <strong>120Ïùº</strong>: Ï§ëÍ∏∞ Ï∂îÏÑ∏ Î∞òÏòÅ, Ïä§Ïúô Ìä∏Î†àÏù¥Îî©Ïóê Ï†ÅÌï©<br/>
                ‚Ä¢ <strong>240Ïùº</strong>: Ïû•Í∏∞ Ìà¨Ïûê Í¥ÄÏ†ê, ÌÅ∞ Í∑∏Î¶º ÌååÏïÖÏóê Ïú†Ïö©<br/>
                ‚Ä¢ Í∏∞Í∞ÑÏù¥ Í∏∏ÏàòÎ°ù ÎÖ∏Ïù¥Ï¶àÍ∞Ä Ï§ÑÍ≥† Ïã†Î¢∞ÎèÑÍ∞Ä ÎÜíÏïÑÏßëÎãàÎã§
              </p>
            </div>
          </CardContent>
        </Card>

        {/* Signal Factors Breakdown */}
        <Card className="border-2 border-indigo-200">
          <CardHeader>
            <CardTitle className="flex items-center gap-2 text-indigo-700">
              <Activity className="w-5 h-5" />
              ÏÑ∏Î∂Ä Î∂ÑÏÑù Ìï≠Î™©
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
              {tradingSignal.factors?.map((factor, idx) => (
                <div key={idx} className="p-3 bg-gray-50 rounded-lg">
                  <div className="flex items-center justify-between mb-1">
                    <span className="text-sm font-medium">{factor.name}</span>
                    <Tooltip>
                      <TooltipTrigger>
                        <Info className="w-3 h-3 text-gray-400" />
                      </TooltipTrigger>
                      <TooltipContent>
                        <p>Weight: {(factor.weight * 100).toFixed(0)}%</p>
                        {factor.description && <p>{factor.description}</p>}
                      </TooltipContent>
                    </Tooltip>
                  </div>
                  <div className="flex items-center gap-2">
                    <div className="flex-1 bg-gray-200 rounded-full h-2">
                      <div 
                        className={`h-2 rounded-full transition-all duration-300 ${
                          factor.value > 0 ? 'bg-green-500' : 'bg-red-500'
                        }`}
                        style={{ 
                          width: `${Math.min(100, Math.abs(factor.value))}%`,
                          marginLeft: factor.value < 0 ? 'auto' : '0'
                        }}
                      />
                    </div>
                    <span className={`text-sm font-bold min-w-[3rem] text-right ${
                      factor.value > 0 ? 'text-green-600' : 
                      factor.value < 0 ? 'text-red-600' : 'text-gray-600'
                    }`}>
                      {typeof factor.value === 'number' ? factor.value.toFixed(0) : factor.value}
                    </span>
                  </div>
                  {factor.phase && (
                    <p className="text-xs text-gray-500 mt-1">{factor.phase}</p>
                  )}
                </div>
              ))}
            </div>

            {/* Backtest Performance */}
            {signalBacktest && (
              <div className="mt-4 p-4 bg-indigo-50 rounded-lg">
                <h4 className="font-medium mb-2 flex items-center gap-2">
                  <Target className="w-4 h-4" />
                  Ïã†Ìò∏ ÏÑ±Í≥º (ÏµúÍ∑º 60Ïùº)
                </h4>
                <div className="grid grid-cols-3 gap-4 text-sm">
                  <div>
                    <span className="text-gray-600">Ï†ïÌôïÎèÑ:</span>
                    <p className="font-bold text-lg">{signalBacktest.winRate}%</p>
                  </div>
                  <div>
                    <span className="text-gray-600">Ï¥ù Ïã†Ìò∏:</span>
                    <p className="font-bold text-lg">{signalBacktest.totalTrades}</p>
                  </div>
                  <div>
                    <span className="text-gray-600">ÏÑ±Í≥µ:</span>
                    <p className="font-bold text-lg">
                      {Math.round((parseFloat(signalBacktest.winRate) || 0) / 100 * (signalBacktest.totalTrades || 0))}
                    </p>
                  </div>
                </div>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Î¶¨Ïä§ÌÅ¨ Í¥ÄÎ¶¨ ÎåÄÏãúÎ≥¥Îìú */}
        <Card className="border-2 border-red-200">
          <CardHeader>
            <CardTitle className="flex items-center gap-2 text-red-700">
              <Shield className="w-5 h-5" />
              Î¶¨Ïä§ÌÅ¨ Í¥ÄÎ¶¨
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              {/* VaR */}
              <div className="p-4 bg-red-50 rounded-lg">
                <div className="flex items-center gap-2 mb-2">
                  <h3 className="font-semibold">ÏùºÏùº VaR</h3>
                  <Tooltip>
                    <TooltipTrigger>
                      <Info className="w-4 h-4 text-gray-500" />
                    </TooltipTrigger>
                    <TooltipContent className="max-w-xs">
                      <p className="font-semibold mb-1">{RISK_INDICATORS.VaR.name}</p>
                      <p>{RISK_INDICATORS.VaR.expert}</p>
                    </TooltipContent>
                  </Tooltip>
                </div>
                <p className="text-2xl font-bold text-red-600">
                  {calculations.dailyVaR?.toFixed(2)}%
                </p>
                <p className="text-sm text-gray-600">
                  Ìè¨ÏßÄÏÖò VaR: {formatKRW(calculations.positionVaR || 0)}
                </p>
              </div>

              {/* Ïú†ÎèôÏÑ± */}
              <div className="p-4 bg-blue-50 rounded-lg">
                <div className="flex items-center gap-2 mb-2">
                  <h3 className="font-semibold">Ïú†ÎèôÏÑ± Î∂ÑÏÑù</h3>
                  <Tooltip>
                    <TooltipTrigger>
                      <Info className="w-4 h-4 text-gray-500" />
                    </TooltipTrigger>
                    <TooltipContent className="max-w-xs">
                      <p className="font-semibold mb-1">{RISK_INDICATORS.LIQUIDITY.name}</p>
                      <p>{RISK_INDICATORS.LIQUIDITY.expert}</p>
                    </TooltipContent>
                  </Tooltip>
                </div>
                <p className="text-sm">ÏùºÌèâÍ∑† Í±∞ÎûòÎåÄÍ∏à</p>
                <p className="text-xl font-bold">{formatKRW(calculations.avgVolume || 0)}</p>
                <p className="text-sm text-blue-600 mt-1">
                  ÏïàÏ†Ñ Í±∞Îûò ÌïúÎèÑ: {formatKRW(calculations.safeTradeSize || 0)}
                </p>
              </div>

              {/* ÏÉ§ÌîÑ ÎπÑÏú® */}
              <div className="p-4 bg-green-50 rounded-lg">
                <div className="flex items-center gap-2 mb-2">
                  <h3 className="font-semibold">ÏÉ§ÌîÑ ÎπÑÏú®</h3>
                  <Tooltip>
                    <TooltipTrigger>
                      <Info className="w-4 h-4 text-gray-500" />
                    </TooltipTrigger>
                    <TooltipContent className="max-w-xs">
                      <p className="font-semibold mb-1">{RISK_INDICATORS.SHARPE.name}</p>
                      <p>{RISK_INDICATORS.SHARPE.expert}</p>
                    </TooltipContent>
                  </Tooltip>
                </div>
                <p className="text-2xl font-bold text-green-600">
                  {calculations.sharpeRatio?.toFixed(2) || "N/A"}
                </p>
                <p className="text-sm text-gray-600">
                  {calculations.sharpeRatio > 2 ? "Ïö∞Ïàò" : 
                   calculations.sharpeRatio > 1 ? "ÏñëÌò∏" : 
                   calculations.sharpeRatio > 0.5 ? "Î≥¥ÌÜµ" : "ÎØ∏Ìù°"}
                </p>
              </div>
            </div>

            {/* Î¶¨Ïä§ÌÅ¨ Í≤ΩÍ≥† */}
            {calculations.dailyVaR > 3 && (
              <div className="mt-4 p-4 bg-red-100 border border-red-300 rounded-lg flex items-center gap-2">
                <AlertTriangle className="w-5 h-5 text-red-600" />
                <p className="text-red-700 font-medium">
                  Í≤ΩÍ≥†: ÎÜíÏùÄ Î≥ÄÎèôÏÑ± Í∞êÏßÄ. Ìè¨ÏßÄÏÖò Ï∂ïÏÜåÎ•º Í≤ÄÌÜ†ÌïòÏÑ∏Ïöî.
                </p>
              </div>
            )}
