// ADX (Average Directional Index) 계산
function calculateADX(data, index, period = 14) {
  if (index < period * 2) return 0;
  let plusDM = 0,
    minusDM = 0,
    tr = 0;
  for (let i = index - period + 1; i <= index; i++) {
    const curr = data[i];
    const prev = data[i - 1];
    const highDiff = curr.high - prev.high;
    const lowDiff = prev.low - curr.low;
    if (highDiff > lowDiff && highDiff > 0) plusDM += highDiff;
    if (lowDiff > highDiff && lowDiff > 0) minusDM += lowDiff;
    const trueRange = Math.max(curr.high - curr.low, Math.abs(curr.high - prev.close), Math.abs(curr.low - prev.close));
    tr += trueRange;
  }
  const plusDI = plusDM / tr * 100;
  const minusDI = minusDM / tr * 100;
  const dx = Math.abs(plusDI - minusDI) / (plusDI + minusDI) * 100;
  return dx;
}

// Stochastic 오실레이터 계산
function calculateStochastic(data, index, period = 14) {
  if (index < period) return 50;
  const slice = data.slice(index - period + 1, index + 1);
  const highs = slice.map(d => d.high);
  const lows = slice.map(d => d.low);
  const highest = Math.max(...highs);
  const lowest = Math.min(...lows);
  const current = data[index].close;
  if (highest === lowest) return 50;
  return (current - lowest) / (highest - lowest) * 100;
}

// 복합 오실레이터 점수
function calculateOscillatorComposite(rsi, mfi, stochastic) {
  let score = 0;

  // RSI 신호
  if (rsi < 30) score += 30;else if (rsi < 40) score += 15;else if (rsi > 70) score -= 30;else if (rsi > 60) score -= 15;

  // MFI 신호
  if (mfi < 20) score += 25;else if (mfi < 35) score += 10;else if (mfi > 80) score -= 25;else if (mfi > 65) score -= 10;

  // Stochastic 신호
  if (stochastic < 20) score += 20;else if (stochastic < 35) score += 10;else if (stochastic > 80) score -= 20;else if (stochastic > 65) score -= 10;

  // 다이버전스 보너스
  if (rsi < 40 && mfi < 40 && stochastic < 40) score *= 1.5;
  if (rsi > 60 && mfi > 60 && stochastic > 60) score *= 1.5;
  return Math.max(-100, Math.min(100, score));
}

